import { createStore } from 'vuex'
import axios from 'axios'
// Create a new store instance.
const store = createStore({
    state() {
        return {
            workouts: null,
            email: null,
            password: null,
            error: null,
            email: null
        }
    },
    mutations: {
        useLogin(state) {
            axios.post('/login', {
                email: state.email,
                password: state.password
            })
                .then((res) => {
                    state.isError = false
                    state.delresult = true
                    localStorage.setItem('user', JSON.stringify(res))
                    state.user = res.data
                    state.results = true
                })
                .catch((err) => {
                    state.isError = true
                    state.results = false
                    store.state.delresult = false
                    state.error = err.response.data.msg
                    console.log(err)
                })

        }
    },
    actions: {
        useLogin(context) {
            context.commit('useLogin')
        }
    }
})
export default store;

