import { createStore } from 'vuex'
import axios from 'axios'
// Create a new store instance.
const store = createStore({
    state() {
        return {
            workouts: null,
            email: null,
            password: null,
            result: null,
            results: false,
            error: null,
            isError: false,
            delresult: false,
            user: null
        }
    },
    mutations: {
        getData(state) {
            if (!state.user) {
                return
            }
            axios.get('http://localhost:9000/books', {
                headers: { 'Authorization': `Bearer ${state.result.data.token}` },
            })
                .then((res) => {
                    state.workouts = res.data
                })
                .catch((err) => console.log(err))
        },
        useSignup(state) {
            axios.post('http://localhost:9000/signin', {
                email: state.email,
                password: state.password,

            })
                .then((res) => {
                    store.state.result = res.data
                    state.isError = false
                    state.delresult = true
                    localStorage.setItem('user', JSON.stringify(res))
                    console.log(state.user);
                    state.results = true
                    state.email = null
                    state.password = null
                })
                .catch((err) => {
                    state.isError = true
                    store.state.delresult = false
                    state.results = false
                    state.error = err.response.data.msg
                    console.log(err.response.data.msg)
                })
        },
        useLogin(state) {
            axios.post('http://localhost:9000/login', {
                email: state.email,
                password: state.password,
            })
                .then((res) => {
                    store.state.result = res.data
                    state.isError = false
                    state.delresult = true
                    state.email = null
                    state.password = null
                    localStorage.setItem('user', JSON.stringify(res))
                    state.results = true
                })
                .catch((err) => {
                    state.isError = true
                    state.results = false
                    store.state.delresult = false
                    state.error = err.response.data.msg
                    console.log(err.response.data.msg)
                })
        },
        getItem(state) {
            state.user = JSON.parse(localStorage.getItem('user'))
            state.results = false
        }
    },
    actions: {
        getData(context) {
            context.commit('getData')
        },
        useSignup(context) {
            context.commit('useSignup')
        },
        useLogin(context) {
            context.commit('useLogin')
        },
        getItem(context) {
            context.commit('getItem')
        }
    }
})
export default store;
