import { createStore } from 'vuex'

// Create a new store instance.
const store = createStore({
    state() {
        return {
            title: null,
            Body: null,
            Content: null,
            Code: null
        }
    },
    methods: {
        increment() {
            this.count++
        }
    }
})
export default store;
