import { createStore } from 'vuex'
import Vue from 'vue'
import axios from 'axios'
// Create a new store instance.
const store = createStore({
    state() {
        return {
            workouts: null,
            email: null,
            password: null,
            result: null,
            results: false,
            error: null,
            isError: false,
            delresult: false,
            user: null
        }
    },
    mutations: {
        async useSignup(state) {
            const response = await fetch('http://localhost:3000/signup', {
                method: 'POST',
                mode: 'cors',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email: state.email, password: state.password })
            })
            const json = await response.json()

            if (!response.ok) {
                state.error = json
                console.log(json);
            }
            if (response.ok) {
                // save the user to local storage
                localStorage.setItem('user', JSON.stringify(json))
                localStorage.setItem('token', JSON.stringify(json.token))
                axios.defaults.headers.common['Authorization'] = JSON.parse(localStorage.getItem('token'))
                state.user = JSON.parse(localStorage.getItem('token'))
                console.log(state.user);
            }
        },
        async getData(state) {
            const response = await fetch('http://localhost:3000/books', {
                mode: 'cors',
                headers: { 'Authorization': `Bearer ${user}` },
            })
            const json = await response.json()

            if (response.ok) {
                console.log(state.user);
                state.workouts = json
            }
        },
        useLogin(state) {
            axios.post('http://localhost:3000/login', {
                email: state.email,
                password: state.password
            })
                .then((res) => {
                    state.isError = false
                    state.delresult = true
                    localStorage.setItem('user', JSON.stringify(res))
                    state.results = true
                })
                .catch((err) => {
                    state.isError = true
                    state.results = false
                    store.state.delresult = false
                    state.error = err.response.data.msg
                    console.log(err.response.data.msg)
                })
        }
    },
    actions: {
        useSignup(context) {
            context.commit('useSignup')
        },
        getData(context) {
            context.commit('getData')
        },
        useLogin(context) {
            context.commit('useLogin')
        }
    }
})
export default store;
