import { createStore } from 'vuex'
import axios from 'axios'
// Create a new store instance.
const store = createStore({
    state() {
        return {
            title: null,
            Body: null,
            Content: null,
            Code: null,
            workouts: null
        }
    },
    mutations: {
        getData(state) {
            axios.get('http://localhost:4000/books')
                .then((res) => {
                    state.workouts = res.data
                })
                .catch((err) => console.log(err))
        },
        handlerDelete(state) {
            axios.delete(`http://localhost:4000/books/${state.workouts._id}`)
                .then((res) => {
                    state.workouts = res.data
                })
                .catch((err) => console.log(err))
        }

    },
    actions: {
        getData(context) {
            context.commit('getData')
        },
        handlerDelete(context) {
            context.commit('handlerDelete')
        }
    }
})
export default store;
