import { createStore } from 'vuex'
import axios from 'axios'
// Create a new store instance.
const store = createStore({
    state() {
        return {
            workouts: null,
            email: null,
            password: null,
            error: null,
            user: null
        }
    },
    mutations: {
        async useSignup(state) {
            const response = await axios.post('http://localhost:3000/api/signup', {
                body: JSON.stringify({ email: state.email, password: state.password }),
                headers: { 'Content-Type': 'application/json' }
            })
            localStorage.setItem('user', JSON.stringify(json.data.token))
        },
        useLogin(state) {
            axios.post('http://localhost:3000/api/login', {
                email: state.email,
                password: state.password
            })
                .then((res) => {
                    state.isError = false
                    state.delresult = true
                    localStorage.setItem('user', JSON.stringify(res))
                    state.user = res.data
                    state.results = true
                })
                .catch((err) => {
                    state.isError = true
                    state.results = false
                    store.state.delresult = false
                    state.error = err.response.data.msg
                    console.log(err)
                })

        }
    },
    actions: {
        useSignup(context) {
            context.commit('useSignup')
        },
        getData(context) {
            context.commit('getData')
        },
        useLogin(context) {
            context.commit('useLogin')
        }
    }
})
export default store;

